PRIVATE_KEY = 'XXXX'

RUN_NODE_THRESHOLD = 10

def main():
    w3 = Web3(Web3.WebsocketProvider('wss://eth-sepolia.g.alchemy.com/v2/Qlr3apDsKG7I7tlI8YGwXtxDVeyT0Kzv'))
    token_contract = w3.eth.contract(address=TOKEN_CONTRACT_ADDRESS, abi=TOKEN_CONTRACT_ABI)
    hosting_protocol_contract = w3.eth.contract(address=HOSTING_PROTOCOL_CONTRACT_ADDRESS, abi=HOSTING_PROTOCOL_CONTRACT_ABI)
    olas_manager_contract = w3.eth.contract(address=OLAS_MANAGER_CONTRACT_ADDRESS, abi=OLAS_MANAGER_CONTRACT_ABI)

    owner = w3.eth.account.from_key(PRIVATE_KEY)

    print(olas_manager_contract.functions.nextUnstakeRequestId().call())

    while True:
        # claim rewards

        # unstake
        nextUnstakeRequestId = olas_manager_contract.functions.nextUnstakeRequestId().call()
        unstake_amount = 0
        for index in range(nextUnstakeRequestId):
            request = olas_manager_contract.functions.unstakeRequests(index).call()[0]
            if request.isClaimed:
                continue
            unstake_amount += request.amount
        
        contract_olas_amount = token_contract.functions.balanceOf(OLAS_MANAGER_CONTRACT_ADDRESS).call()
        contract_olas_locked_amount = olas_manager_contract.functions.olasLocked().call()
        if unstake_amount > contract_olas_amount - contract_olas_locked_amount:
            # stop Nodes
            contract_olas_amount +=  w3.to_wei(100, "ether")

        for index in range(nextUnstakeRequestId):
            request = olas_manager_contract.functions.unstakeRequests(index).call()[0]
            if request.isClaimed:
                continue
            
            tx = olas_manager_contract.functions.fillUnstakeRequest(index).build_transaction({
                "from": owner.address,
                "nonce": w3.eth.get_transaction_count(owner.address)
            })
            signed_tx = owner.sign_transaction(tx)
            tx_hash = w3.eth.send_raw_transaction(signed_tx.rawTransaction)
            w3.eth.wait_for_transaction_receipt(tx_hash)

        # stake
        stake_amount = w3.to_wei(RUN_NODE_THRESHOLD, "ether")
        contract_olas_locked_amount = olas_manager_contract.functions.olasLocked().call()
        while token_contract.functions.balanceOf(OLAS_MANAGER_CONTRACT_ADDRESS).call() - contract_olas_locked_amount > stake_amount:
            tx = olas_manager_contract.functions.lockStaking(stake_amount).build_transaction({
                "from": owner.address,
                "nonce": w3.eth.get_transaction_count(owner.address)
            })
            signed_tx = owner.sign_transaction(tx)
            tx_hash = w3.eth.send_raw_transaction(signed_tx.rawTransaction)
            w3.eth.wait_for_transaction_receipt(tx_hash)

            tx = hosting_protocol_contract.functions.runNode(token_contract.address, stake_amount, "HUI").build_transaction({
                "from": owner.address,
                "nonce": w3.eth.get_transaction_count(owner.address)
            })
            signed_tx = owner.sign_transaction(tx)
            tx_hash = w3.eth.send_raw_transaction(signed_tx.rawTransaction)
            w3.eth.wait_for_transaction_receipt(tx_hash)
            
        # time.sleep(12 * 60 * 60)
        time.sleep(3)


if __name__ == "__main__":
    main()
